import react, { Component } from 'react';
import axios from 'axios';
import ReactFileReader from 'react-file-reader';
import './App.css';
class SubmitComponent extends Component{

constructor(props){
  super(props);
    this.state = {
    file : ""
  }
}

remove = files => {

  this.setState({
    file: " "
  })
}

handleFiles = files => {
  this.setState({
    file: files.base64
  })

}
render(){
  return(
    <div className="files">
    <ReactFileReader fileTypes={[".pdf",".docx"]} base64={true} multipleFiles={true} handleFiles={this.handleFiles}>
    <button className='btn' >Upload</button>
    </ReactFileReader>
    <button className="btn" onClick={this.remove} > Remove </button>
    <button className="btn" onClick={this.submit} > submit </button>
      <p>Read</p>
    <iframe src={this.state.file} frameBorder="0" height="800" width="50%" />
    </div>
  );
      }



}

export default SubmitComponent;
