import react, { Component } from 'react';
import axios from 'axios';
import ReactFileReader from 'react-file-reader';
import './App.css';
class SubmitComponent extends Component{

constructor(props){
  super(props);
    this.state = {
    file : null
  }

}

handleFile(e){
  let myfile=e.target.files[0];
  this.setState({file:myfile});

 }
 hundleUpload(e){
   let myfile = this.state.file;
   let formdata= new FormData();
   formdata.append('file',myfile);
   if(myfile==null){
     console.Console("Faut choisir un fichier");
  }else{
   axios({url:"http://localhost:8080/api/CV/add",data:formdata}).then((res)=>{
     console.log(res);
   });
  }

 }
render(){
  return(
    <div className="Submitcomponent">
    <h1>The Form</h1>
    <form>
      <div>
        <label> Select file </label>
        <input type="file" name="file" onChange={(e)=>this.handleFile(e)}/>
      </div>
      <br/>
      <button onClick={(e)=>this.hundleUpload(e)}> upload</button>
    </form>
    </div>
  );
      }



}

export default SubmitComponent;
